public class Q3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		double d = 1.1;
		float f = 3.1f;
		
		double result1 = i + d;
		double result2 = f +d;
		float result3 = i + f;
		
		System.out.println("int + double: " + result1);
		System.out.println("float + double: " + result2);
		System.out.println("int + float: " + result3);
	}

}
