package com.assign4;

public class Q4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 27;
		double d = i;
		float f = i;
		String  s = Integer.toString(i);
		boolean b = i > 0;
		
		System.out.println("Original value: " + i);
		System.out.println("convert into double: " + d);
		System.out.println("convert into float: " + f);
		System.out.println("convert into string: " + s);
		System.out.println("Convert into boolean: " + b);
		
	}

}
