package CDAC_Assi3;

//sum of the first 50 natural numbers
public class Q1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 50; 
        int sum = 0;
        for (int i = 1; i <= n; i++) {
            sum += i;
        }
        System.out.println("The sum of: " + sum);
	}

}
